import requests


#Input: coordinates in list with tuples [(lat, lon), (..), etc.]. NB. The API key may have restrictions.

def get_elevations(coordinates):
    # Your Google API key
    api_key = 'AIzaSyCHSK14AQNBQt4QOk7unFiJZcm6BpfyAqI'  
    
    # Create an empty list to store the elevations
    elevations = []
    
    for lat, lon in coordinates:
        # Google Maps Elevation API endpoint
        url = f"https://maps.googleapis.com/maps/api/elevation/json?locations={lat},{lon}&key={api_key}"
        
        # Send the request to the API
        response = requests.get(url)
        
        # Check if the response is valid
        if response.status_code == 200:
            data = response.json()
            if data["status"] == "OK":
                # Extract elevation data
                elevation = data["results"][0]["elevation"]
                elevations.append(elevation)
            else:
                elevations.append(f"Error: {data['status']}")
        else:
            elevations.append(f"Error: {response.status_code}")
    
    return elevations

